mapper.select:
select r.ID_SLAVE as slaveId, r.HOST_NAME as hostName, r.`PORT` as port, r.USERNAME as username, r.PASSWORD as password from r_slave r WHERE 1=1
AND r.ID_SLAVE IN ( select ID_SLAVE from d_user_group_slaves_relation where USER_GROUP_NAME=#{2} )
limit #{0},#{1}

mapper.update:
UPDATE r_slave
NAME=#{name}, HOST_NAME=#{hostName}, PORT=#{port}, WEB_APP_NAME=#{webappName}, USERNAME=#{username}, PASSWORD=#{password}, PROXY_HOST_NAME=#{proxyHostname}, PROXY_PORT=#{proxyPort}, NON_PROXY_HOSTS=#{nonproxyHosts}, MASTER=#{master}
WHERE ID_SLAVE=#{slaveId}
